mapper.select:
SELECT *
FROM	
	(SELECT result1.*, ROWNUM as rnum
	FROM
		(SELECT num, teacher, writer,title, describe, imagePath, videoPath, viewCount, regdate, large_category,small_category
		FROM LECTURE


					WHERE large_category=#{large_category}


					WHERE large_category=#{large_category} AND small_category=#{small_category}


		ORDER BY num DESC) result1)
WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}

SELECT num, teacher, writer, title, describe, imagePath, videoPath, viewCount, regdate, large_category,small_category
FROM LECTURE
WHERE num = #{num}

mapper.update:
UPDATE LECTURE
SET viewCount=viewCount+1
WHERE num=#{num}

UPDATE LECTURE
SET title= #{title}, teacher=#{teacher},imagePath=#{imagePath,jdbcType=VARCHAR},describe=#{describe}, videoPath=#{videoPath}, large_category=#{large_category},small_category=#{small_category}
WHERE num = #{num}
