mapper.update:
UPDATE LECTURE_STUDENT
SET complete=#{complete}
WHERE ref_group=#{ref_group} AND id=#{id}

mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM as rnum FROM (SELECT users.id,TO_CHAR(birth,'YYYY-MM-DD') birth, phone, email, regdate, large_category, small_category FROM users JOIN LECTURE_STUDENT ON users.id=LECTURE_STUDENT.id JOIN LECTURE on LECTURE.num=LECTURE_STUDENT.ref_group
WHERE large_category=#{large_category}
WHERE large_category=#{large_category} AND small_category=#{small_category}
ORDER BY users.id DESC) result1) WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}
